(function(){
function plural(n,singular,plural){return n===1?singular:plural}
function enhance(root){
  if(!root){return}
  var comments=root.querySelectorAll('.comment-thread .comment,.comment');
  var isDark=document.documentElement.classList.contains('is-dark');
  comments.forEach(function(comment){
    var blogAuthorIcon=comment.querySelector('.comment-header  .icon.blog-author,.comment-replies .icon.blog-author,.comments-content .icon.blog-author');
    var userElement=comment.querySelector('.comment-header .user,.user');
    if(blogAuthorIcon){
      blogAuthorIcon.style.cssText='display:inline-block!important;visibility:visible!important;width:16px!important;height:16px!important;background:#000!important;color:#fff!important;border-radius:50%!important;text-align:center!important;line-height:16px!important;font-size:11px!important;font-weight:900!important;margin-left:5px!important;vertical-align:middle!important;opacity:1!important;position:relative!important;z-index:1000!important;overflow:visible!important;box-sizing:border-box!important;background-image:none!important;';
      if(!blogAuthorIcon.querySelector(':before')){
        blogAuthorIcon.setAttribute('title','Verified Author');
      }
    }
    if(userElement && !userElement.querySelector('.author-badge-verify') && userElement.textContent && userElement.textContent.trim()){
      var badge=document.createElement('span');
      badge.className='author-badge-verify verified-badge';
      badge.title='Verified Author';
      badge.setAttribute('aria-label','Verified Author');
      // Note: Checkmark is added via CSS :before pseudo-element to avoid duplication
      var bgColor=isDark?'#fff':'#000';
      var fgColor=isDark?'#000':'#fff';
      badge.style.cssText='display:inline-block!important;width:16px!important;height:16px!important;min-width:16px!important;min-height:16px!important;max-width:16px!important;max-height:16px!important;background:'+bgColor+'!important;color:'+fgColor+'!important;border-radius:50%!important;text-align:center!important;line-height:16px!important;font-size:11px!important;font-weight:900!important;margin-left:5px!important;vertical-align:middle!important;opacity:1!important;visibility:visible!important;position:relative!important;z-index:1000!important;flex-shrink:0!important;overflow:visible!important;box-sizing:border-box!important;';
      var authorLink=userElement.querySelector('a');
      if(authorLink && authorLink.nextSibling){
        userElement.insertBefore(badge,authorLink.nextSibling);
      }else if(authorLink){
        userElement.appendChild(badge);
      }else{
        var firstText=Array.from(userElement.childNodes).find(function(n){return n.nodeType===3&&n.textContent.trim();});
        if(firstText){
          userElement.insertBefore(badge,firstText.nextSibling);
        }else{
          userElement.appendChild(badge);
        }
      }
    }
    if(comment.getAttribute('data-replies-initialized')==='true'){return}
    var anchors=Array.prototype.slice.call(comment.querySelectorAll('a'));
    var replyAnchors=anchors.filter(function(a){ return (a.textContent||'').trim().toLowerCase()==='reply' || a.classList.contains('comment-reply') || (a.getAttribute('href')||'').indexOf('#comment-form')===0; });
    if(replyAnchors.length>1){
      var preferred=comment.querySelector('.comment-actions a')||replyAnchors[0];
      replyAnchors.forEach(function(a){ if(a!==preferred){ a.remove(); } });
    }
    var replies=comment.querySelector('.comment-replies');
    if(!replies){return}
    var count=(replies.querySelectorAll(':scope > ol > li.comment').length)||0;
    if(count===0){return}
    replies.hidden=true;
    var actions=actionsEl||comment.querySelector('.comment-actions');
    var target=actions||comment.querySelector('.comment-block')||comment;
    var link=document.createElement('a');
    link.href='#';
    link.className='comment-toggle-replies';
    function setText(expanded){
      link.textContent=expanded?plural(count,'Hide reply','Hide replies'):'View '+count+' '+plural(count,'reply','replies')
    }
    setText(false);
    link.addEventListener('click',function(e){
      e.preventDefault();
      var expanded=replies.hidden===false;
      replies.hidden=expanded?true:false;
      setText(!expanded);
    });
    if(actions){
      actions.appendChild(link);
    }else{
      var wrap=document.createElement('div');
      wrap.className='comment-actions';
      wrap.appendChild(link);
      target.appendChild(wrap);
    }
    comment.setAttribute('data-replies-initialized','true');
  });
}
function addBadgesToComments(){
  var commentsRoot=document.getElementById('comments');
  if(!commentsRoot){return}
  var holder=document.getElementById('comment-holder')||commentsRoot;
  enhance(commentsRoot);
  var obs=new MutationObserver(function(){enhance(commentsRoot)});
  obs.observe(holder,{childList:true,subtree:true});
  // Also check immediately for any existing comments
  setTimeout(function(){enhance(commentsRoot);},50);
  setTimeout(function(){enhance(commentsRoot);},100);
  setTimeout(function(){enhance(commentsRoot);},300);
  setTimeout(function(){enhance(commentsRoot);},500);
  setTimeout(function(){enhance(commentsRoot);},1000);
  setTimeout(function(){enhance(commentsRoot);},2000);
}
function ready(){
  addBadgesToComments();
  // Also run periodically to catch late-loading comments
  setInterval(function(){
    var commentsRoot=document.getElementById('comments');
    if(commentsRoot){enhance(commentsRoot);}
  },2000);
}
if(document.readyState==='loading'){
  document.addEventListener('DOMContentLoaded',ready);
}else{ready()}
// Also run on page load for dynamically loaded comments
window.addEventListener('load',function(){
  setTimeout(addBadgesToComments,100);
  setTimeout(addBadgesToComments,500);
});
})();

