(function(){
function buildMobileLogo(){
  var mobile=document.querySelector('.mobile-logo');
  if(!mobile){return}
  var mainImg=document.querySelector('.main-logo img');
  var mainLink=document.querySelector('.main-logo a.homepage');
  mobile.innerHTML='';
  if(mainImg){
    var img=document.createElement('img');
    img.alt=mainImg.getAttribute('alt')||'';
    img.src=mainImg.getAttribute('src');
    img.style.display='block';
    img.style.maxHeight='var(--logo)';
    img.style.opacity='1';
    img.style.visibility='visible';
    var isDark=document.documentElement.classList.contains('is-dark');
    img.style.filter=isDark?'invert(0)':'invert(1)';
    if(mainLink){
      var a=document.createElement('a');
      a.className='logo-img';
      a.href=mainLink.getAttribute('href');
      a.rel='home';
      a.appendChild(img);
      mobile.appendChild(a);
    }else{
      mobile.appendChild(img);
    }
  }
}
function run(){
  buildMobileLogo();
  var toggle=document.querySelector('.menu-toggle');
  if(toggle){ toggle.addEventListener('click',function(){ setTimeout(buildMobileLogo,0); }); }
}
if(document.readyState==='loading'){
  document.addEventListener('DOMContentLoaded',run);
}else{run()}
})();

